namespace Core.Extension;

public static class StringExtensions
{
	/// <summary>
	///     Retrieve a substring with start and end index
	/// </summary>
	/// <param name="source">String to be cut.</param>
	/// <param name="startIndex">Start index.</param>
	/// <param name="endIndex">End index.</param>
	/// <returns>Result substring.</returns>
	public static string SubString(this string source, int startIndex, int endIndex)
	{
		return source.Substring(startIndex, endIndex - startIndex);
	}

	/// <summary>
	///     Reverse order of string
	/// </summary>
	/// <param name="source">String to be reversed.</param>
	/// <returns>Result reversed string.</returns>
	public static string Reverse(this string source)
	{
		var arr = source.ToCharArray();
		Array.Reverse(arr);
		return new(arr);
	}

	/// <summary>
	///	    Indicates whether the specified string is null or an System.String.Empty string AFTER trimming.
	/// </summary>
	/// <param name="source">The string to test..</param>
	/// <returns>true if the source parameter is null or an empty string (""); otherwise, false..</returns>
	public static bool IsNullOrEmpty(this string source)
	{
		return source == null || string.IsNullOrWhiteSpace(source);
	}

	/// <summary>
	///     Indicates whether the specified string is not null and an System.String.Empty string AFTER trimming.
	/// </summary>
	/// <param name="source">The string to test..</param>
	/// <returns>true if the source parameter is not null and an empty string (""); otherwise, false..</returns>
	public static bool IsNotNullNorEmpty(this string source)
	{
		return !IsNullOrEmpty(source);
	}

	/// <summary>
	///     Quote string ""
	/// </summary>
	/// <param name="source">The original string to be quoted .</param>
	/// <returns>Result string.</returns>
	public static string Quote(this string source)
	{
		return $"\"{source}\"";
	}

	/// <summary>
	///     Trim current string and add suffix
	/// </summary>
	/// <param name="source">The original string.</param>
	/// <param name="maxLength">Max length to trim.</param>
	/// <param name="suffix">The suffix of string.</param>
	/// <returns>Result string.</returns>
	public static string TrimLongText(this string source, int maxLength, string suffix = "...")
	{
		var longerMaxLength = source.Length > maxLength + suffix.Length;
		return $"{source[..(longerMaxLength ? maxLength : source.Length)]}{(longerMaxLength ? suffix : string.Empty)}";
	}

	public static string Trim(this string source, string trimString)
	{
		return source.Trim(trimString.ToCharArray());
	}

	/// <summary>
	///     Convert the string to Hex string
	/// </summary>
	/// <param name="source">Original string.</param>
	/// <returns>Result Hex string.</returns>
	public static string ToHex(this string source)
	{
		var charArray = source.ToCharArray();
		var builder = new StringBuilder();
		foreach (var hex in charArray.Select(Convert.ToInt32).Select(num => $"%{num}"))
		{
			builder.Append(hex);
		}

		return builder.ToString();
	}

	/// <summary>
	/// 	Return first character upper case
	/// </summary>
	/// <param name="source">.</param>
	/// <returns>.</returns>
	public static string ToFirstCharUpper(this string source)
	{
		return source.IsNullOrEmpty() ? string.Empty : source.First().ToString().ToUpper() + string.Join(string.Empty, source.Skip(1));
	}

	/// <summary>
	///     Generate a random string with length
	/// </summary>
	/// <param name="length">Length of random string want to generate.</param>
	/// <param name="safeString">The string will contains special characters or not.</param>
	/// <returns>Result generated string.</returns>
	public static string Random(int length, bool safeString = true)
	{
		var random = new Random();
		var charPool = safeString
			? "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz1234567890"
			: "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz1234567890!@#$%^&*()";
		var rs = new StringBuilder();

		while (length-- > 0)
		{
			rs.Append(charPool[(int) (random.NextDouble() * charPool.Length)]);
		}

		return rs.ToString();
	}

	/// <summary>
	///     Generate a random int with fixed length.
	/// </summary>
	/// <param name="length">Length of random string want to generate</param>
	/// <returns>Result generated string</returns>
	public static string RandomIntFixedLength(int length)
	{
		var random = new Random();
		var charPool = "1234567890";
		var rs = new StringBuilder();

		while (length-- > 0)
		{
			rs.Append(charPool[(int) (random.NextDouble() * charPool.Length)]);
		}

		return rs.ToString();
	}

	/// <summary>
	///     Generate current UTC time stamp in ddMMyyyyhhmmss format
	/// </summary>
	/// <returns>Timestamp string.</returns>
	public static string TimeStamp()
	{
		return DateTime.UtcNow.ToString("yyyyMMddhhmmss");
	}

	public static bool Match(this string source, string regexPattern)
	{
		var regex = new Regex(regexPattern);
		return regex.IsMatch(source);
	}

	public static bool IsEmail(this string source)
	{
		return source.Match(RegExConstants.EMAIL);
	}

	public static string ExtractRegex(this string source, string regexPattern, int matchIndex)
	{
		var regex = new Regex(regexPattern);
		var groups = regex.Matches(source);

		return groups.Count == 0 ? string.Empty : groups[0].Groups[matchIndex].Value;
	}

	public static bool IsPhoneNumber(this string source, out double phoneNumber)
	{
		var result = double.TryParse(source, out phoneNumber);
		if (result)
		{
			phoneNumber = Math.Truncate(phoneNumber);
		}

		return result;
	}

	public static bool IsValidPassword(this string source, string regexPattern = "")
	{
		return source.Match(regexPattern.IsNullOrEmpty() ? RegExConstants.PASSWORD : regexPattern);
	}

	public static GroupCollection? Extract(this string source, string regexPattern)
	{
		var regex = new Regex(regexPattern);
		var match = regex.Match(source);
		return match.Success ? match.Groups : null;
	}

	public static bool EqualsCI(this string source, string comparedString)
	{
		return source.Equals(comparedString, StringComparison.OrdinalIgnoreCase);
	}

	public static bool ContainsCI(this string source, string comparedString)
	{
		return source != null && comparedString != null && source.ToLower().Contains(comparedString.ToLower());
	}

	public static bool StartsWithCI(this string source, string comparedString)
	{
		return source != null && comparedString != null && source.ToLower().StartsWith(comparedString.ToLower());
	}

	public static IEnumerable<string> ToList(this string source, char separator = ',')
	{
		return source.IsNullOrEmpty() ? Enumerable.Empty<string>() : source.Split(separator).ToList();
	}

	public static string ToLowerSafe(this string source)
	{
		return source.ToLower();
	}

	public static string ToUpperSafe(this string source)
	{
		return source.ToUpper();
	}

	public static DateTime ToDateTime(this string source, string dateFormat)
	{
		DateTime.TryParseExact(source, dateFormat, CultureInfo.InvariantCulture, DateTimeStyles.None, out var date);
		return date;
	}

	public static DateTime FromUnixDate(this string source)
	{
		var _ = long.TryParse(source, out var value);
		return value.ToDateTime();
	}

	public static string FromMySqlUuidToUuidString(this string source)
	{
		Guid.TryParseExact(source, "N", out var guid);
		return guid.ToString();
	}

	public static string ToMySqlUuidString(this string source)
	{
		return source.Replace("-", string.Empty);
	}

	public static Guid? ToUuid(this string source)
	{
		var isValid = Guid.TryParse(source, out var guid);
		return isValid ? guid : null;
	}

	public static bool In(this string source, params string[] list)
	{
		return list.Any(s => s.EqualsCI(source));
	}

	/// <summary>
	/// Similar to string.Format()
	/// </summary>
	/// <param name="source">.</param>
	/// <param name="values">.</param>
	/// <returns>.</returns>
	public static string ApplyFormat(this string source, params object[] values)
	{
		return string.Format(source, values);
	}

	public static string RemoveSpecialCharacters(this string source)
	{
		var sb = new StringBuilder();

		foreach (var c in source.Where(c => c is >= '0' and <= '9' or >= 'A' and <= 'Z' or >= 'a' and <= 'z' or '.' or '_'))
		{
			sb.Append(c);
		}

		return sb.ToString();
	}

	public static bool IsBase64(this string source, out string? bytes)
	{
		bytes = null;

		if (source.IsNullOrEmpty()
		    || source.Length % 4 != 0
		    || source.Contains(' ')
		    || source.Contains('\t')
		    || source.Contains('\r')
		    || source.Contains('\n'))
		{
			return false;
		}

		try
		{
			bytes = DecodeBase64(source);
			return true;
		}
		catch (Exception)
		{
			// Handle the exception
		}

		return false;
	}

	public static string EncodeBase64(this string source)
	{
		var plainTextBytes = Encoding.UTF8.GetBytes(source);
		return Convert.ToBase64String(plainTextBytes);
	}

	public static string DecodeBase64(this string source)
	{
		var base64EncodedBytes = Convert.FromBase64String(source);
		return Encoding.UTF8.GetString(base64EncodedBytes);
	}

	public static string Compress(this string source)
	{
		var buffer = Encoding.UTF8.GetBytes(source);
		using var memoryStream = new MemoryStream();

		using (var gZipStream = new GZipStream(memoryStream, CompressionMode.Compress, true))
		{
			gZipStream.Write(buffer, 0, buffer.Length);
		}

		memoryStream.Position = 0;

		var compressedData = new byte[memoryStream.Length];
		memoryStream.Read(compressedData, 0, compressedData.Length);

		var gZipBuffer = new byte[compressedData.Length + 4];
		Buffer.BlockCopy(compressedData, 0, gZipBuffer, 4, compressedData.Length);
		Buffer.BlockCopy(BitConverter.GetBytes(buffer.Length), 0, gZipBuffer, 0, 4);
		return Convert.ToBase64String(gZipBuffer);
	}

	public static string Decompress(this string source)
	{
		var gZipBuffer = Convert.FromBase64String(source);

		using var memoryStream = new MemoryStream();

		var dataLength = BitConverter.ToInt32(gZipBuffer, 0);
		memoryStream.Write(gZipBuffer, 4, gZipBuffer.Length - 4);

		var buffer = new byte[dataLength];

		memoryStream.Position = 0;

		using var gZipStream = new GZipStream(memoryStream, CompressionMode.Decompress);
		gZipStream.Read(buffer, 0, buffer.Length);

		return Encoding.UTF8.GetString(buffer);
	}

	public static byte[]? ToByteArray(this string source)
	{
		byte[]? result = null;

		if (source != null)
		{
			result = Encoding.UTF8.GetBytes(source);
		}

		return result;
	}

	public static bool ToBoolean(this string source)
	{
		bool result;

		if (source.In("1", "True", "true", "Yes", "yes"))
		{
			result = true;
		}
		else
		{
			var _ = bool.TryParse(source, out result);
		}

		return result;
	}

	public static string ToSnakeCase(this string source)
	{
		return string.Concat(source.Select((x, i) => i > 0 && char.IsUpper(x) ? $"_{x}" : x.ToString())).ToLower();
	}

	public static string ToPascalCase(this string source)
	{
		var split = source.Split([
			"_"
		], StringSplitOptions.RemoveEmptyEntries);
		return split.Select(s => char.ToUpperInvariant(s[0]) + s.Substring(1, s.Length - 1)).Aggregate(string.Empty, (s1, s2) => s1 + s2);
	}

	public static string ToCamelCase(this string source)
	{
		return string.IsNullOrEmpty(source) || source.Length <= 1
			? source
			: char.ToLowerInvariant(source[0]) + source[1..];
	}

	public static string HashSha512(this string source)
	{
		using var hash = SHA512.Create();
		var bytes = hash.ComputeHash(Encoding.UTF8.GetBytes(source));

		var builder = new StringBuilder();
		foreach (var t in bytes)
		{
			builder.Append(t.ToString("x2"));
		}

		return builder.ToString();
	}

	public static string Encrypt(this string source)
	{
		var reversedString = source.Reverse();
		var finalString = source + reversedString;
		var arr = Encoding.UTF8.GetBytes(finalString);
		return Convert.ToBase64String(arr).Reverse();
	}

	/// <summary>
	/// https://damienbod.com/2020/08/19/symmetric-and-asymmetric-encryption-in-net-core/
	/// https://docs.microsoft.com/en-us/dotnet/standard/security/encrypting-data#symmetric-encryption
	/// </summary>
	/// <param name="source"></param>
	/// <param name="pairKeys"></param>
	/// <returns></returns>
	public static string Encrypt(this string source, out (string key, string iv) pairKeys)
	{
		pairKeys.key = Guid.NewGuid().ToString("N");

		var cipher = Aes.Create();
		cipher.Mode = CipherMode.CBC;
		cipher.Padding = PaddingMode.ISO10126;
		cipher.Key = Convert.FromBase64String(pairKeys.key);

		pairKeys.iv = Convert.ToBase64String(cipher.IV);

		var cryptTransform = cipher.CreateEncryptor();
		var plaintext = Encoding.UTF8.GetBytes(source);
		var cipherText = cryptTransform.TransformFinalBlock(plaintext, 0, plaintext.Length);

		return Convert.ToBase64String(cipherText);
	}

	public static string Decrypt(this string source)
	{
		try
		{
			var arr = Convert.FromBase64String(source.Reverse());
			var combinedString = Encoding.UTF8.GetString(arr);
			return combinedString[..(combinedString.Length / 2)];
		}
		catch (Exception)
		{
			return string.Empty;
		}
	}

	/// <summary>
	/// https://damienbod.com/2020/08/19/symmetric-and-asymmetric-encryption-in-net-core/
	/// https://docs.microsoft.com/en-us/dotnet/standard/security/encrypting-data#symmetric-encryption
	/// </summary>
	/// <param name="source"></param>
	/// <param name="key"></param>
	/// <param name="iv"></param>
	/// <returns></returns>
	public static string Decrypt(this string source, string key, string iv)
	{
		var cipher = Aes.Create();
		cipher.Mode = CipherMode.CBC; // Ensure the integrity of the ciphertext if using CBC
		cipher.Padding = PaddingMode.ISO10126;
		cipher.Key = Convert.FromBase64String(key);
		cipher.IV = Convert.FromBase64String(iv);

		var cryptTransform = cipher.CreateDecryptor();
		var encryptedBytes = Convert.FromBase64String(source);
		var plainBytes = cryptTransform.TransformFinalBlock(encryptedBytes, 0, encryptedBytes.Length);

		return Encoding.UTF8.GetString(plainBytes);
	}

	public static DateTime ToDateTime(this string source)
	{
		return Convert.ToDouble(source).ToDateTime();
	}

	public static string Escape(this string source)
	{
		var sb = new StringBuilder();

		foreach (var c in source)
		{
			if (c > 127)
			{
				var encodedValue = "\\u" + ((int) c).ToString("x4");
				sb.Append(encodedValue);
			}
			else
			{
				sb.Append(c);
			}
		}

		return sb.ToString();
	}

	public static string UnEscape(this string source)
	{
		return Regex.Replace(source, @"\\u(?<Value>[a-zA-Z0-9]{4})", m => ((char) int.Parse(m.Groups["Value"].Value, NumberStyles.HexNumber)).ToString());
	}

	/// <summary>
	/// Replace all non-latin characters, special characters, including {space}, to replaceString
	/// </summary>
	/// <param name="source"></param>
	/// <param name="replaceString">Default is _</param>
	/// <returns></returns>
	public static string ToSafeString(this string source, string replaceString = "_")
	{
		return Regex.Replace(source, @"[^a-zA-z0-9]+", replaceString);
	}

	public static string SafeEndpoint(this string source)
	{
		return source.Trim('/');
	}

	public static bool IsSnakeCase(this string source)
	{
		return Regex.Match(source, "^[a-z0-9]+(_[a-z0-9]+)*$").Success;
	}

	public static Stream ToStream(this string source)
	{
		using var stream = new MemoryStream();
		using var writer = new StreamWriter(stream);
		writer.Write(source);
		writer.Flush();
		stream.Position = 0;

		return stream;
	}

	public static void ActionInStream(this string source, Action<MemoryStream> action)
	{
		using var stream = new MemoryStream();
		using var writer = new StreamWriter(stream);
		writer.Write(source);
		writer.Flush();
		stream.Position = 0;

		action(stream);
	}

	public static Task ActionInStreamAsync(this string source, Func<MemoryStream, Task> funcAsync)
	{
		using var stream = new MemoryStream();
		using var writer = new StreamWriter(stream);
		writer.Write(source);
		writer.Flush();
		stream.Position = 0;

		funcAsync(stream);

		return Task.CompletedTask;
	}

